const a="https://www.fueleconomy.gov/ws/rest";async function s(){try{const r=await(await fetch(`${a}/vehicle/menu/year`,{headers:{Accept:"application/json"}})).json();return r.menuItem?r.menuItem.map(t=>parseInt(t.value)):[]}catch(e){return console.error("Error fetching years:",e),[]}}async function c(e){try{const t=await(await fetch(`${a}/vehicle/menu/make?year=${e}`,{headers:{Accept:"application/json"}})).json();return t.menuItem?Array.isArray(t.menuItem)?t.menuItem:[t.menuItem]:[]}catch(r){return console.error("Error fetching makes:",r),[]}}async function i(e,r){try{const n=await(await fetch(`${a}/vehicle/menu/model?year=${e}&make=${r}`,{headers:{Accept:"application/json"}})).json();return n.menuItem?Array.isArray(n.menuItem)?n.menuItem:[n.menuItem]:[]}catch(t){return console.error("Error fetching models:",t),[]}}async function u(e,r,t){try{const o=await(await fetch(`${a}/vehicle/menu/options?year=${e}&make=${r}&model=${t}`,{headers:{Accept:"application/json"}})).json();return o.menuItem?Array.isArray(o.menuItem)?o.menuItem:[o.menuItem]:[]}catch(n){return console.error("Error fetching vehicle options:",n),[]}}async function m(e){try{return await(await fetch(`${a}/vehicle/${e}`,{headers:{Accept:"application/json"}})).json()}catch(r){return console.error("Error fetching vehicle:",r),null}}async function p(){try{return await(await fetch(`${a}/fuelprices`,{headers:{Accept:"application/json"}})).json()}catch(e){return console.error("Error fetching fuel prices:",e),null}}export{p as a,c as b,i as c,u as d,m as e,s as g};
